function addForm() {
    save_method = "add";
    $('input[name=_method]').val('POST');
    $('#modal-form').modal('show');
    $('#modal-form form')[0].reset();
    $('.modal-title').text('Crear usuario');
}
function editForm(id) {
    $('#id').val(id);
    save_method = 'edit';
    $('input[name=_method]').val('PATCH');
    $('#modal-form form')[0].reset();
    $.ajax({
        url: "/user/" + id + "/edit",
        type: "GET",
        dataType: "JSON",
        success: function(data) {
            $('#modal-form').modal('show');
            $('.modal-title').text('Editar usuario');
            $('#name').val(data.name);
            $('#last_name').val(data.last_name);
            $('#password').val(data.password);
            $('#type').val(data.type);
            $('#email').val(data.email);
        },
        error : function() {
            alert("Nothing Data");
        }
    });
}
function deleteData(id){
    var csrf_token = $('meta[name="csrf-token"]').attr('content');
    swal({
        title: 'ESTA USTED SEGURO DE ELIMINAR EL USUARIO?',
        text: "NO PODRA REVERTIRLO!",
        type: 'warning',
        showCancelButton: true,
        cancelButtonColor: '#d33',
        cancelButtonText: 'CANCELAR',
        confirmButtonColor: '#605ca8',
        confirmButtonText: 'SI, ELIMINARLO!'
    }).then(function (r) {
        if (r.value){
            $.ajax({
                url: "/user/" + id,
                type : "POST",
                data : {'_method' : 'DELETE', '_token' : csrf_token},
                success : function(data) {
                    table.ajax.reload();
                    swal({
                        title: 'Success!',
                        text: data.message,
                        type: 'success',
                        timer: '1500'
                    })
                    table.draw();
                },
                error : function () {
                    swal({
                        title: 'Oops...',
                        text: data.message,
                        type: 'error',
                        timer: '1500'
                    })
                }
            });
        }
    });
}
$(function(){
    $('#modal-form form').validator().on('submit', function (e) {
        if (!e.isDefaultPrevented()){
            var id = $('input#id').val();
            if (save_method == 'add') url = "/user";
            else url = "/user/" + id;
            $.ajax({
                url : url,
                type : "POST",
                data: new FormData($("#modal-form form")[0]),
                contentType: false,
                processData: false,
                success : function(data) {
                    $('#modal-form').modal('hide');
                    table.ajax.reload();
                    swal({
                        title: 'Listo!',
                        text: data.message,
                        type: 'success',
                        timer: '1500'
                    })
                },
                error : function(data){
                    swal({
                        title: 'Oops...',
                        text: data.message,
                        type: 'error',
                        timer: '1500'
                    })
                }
            });
            return false;
        }
    });
});
